import React from "react";
import Layout from "../components/layout/layout";
import useFetch from "../hooks/useFetch";
import "./Characters.css"; // Asegúrate de crear este archivo con estilos

const Characters = () => {
  const { data, loading, error } = useFetch("https://dragon-ball-api.herokuapp.com/api/character");

  return (
    <Layout>
      <h2>Characters (Productos)</h2>
      {loading && <p>Cargando...</p>}
      {error && <p>Error al cargar datos</p>}
      {data && (
        <div className="characters-list">
          {data.map((character) => (
            <div key={character.id} className="character-card">
              <img src={character.image} alt={character.name} />
              <h3>{character.name}</h3>
            </div>
          ))}
        </div>
      )}
    </Layout>
  );
};

export default Characters;
