import React, { useState } from "react";
import Layout from "../components/layout/Layout";

const Orders = () => {
  const [orders, setOrders] = useState([
    { id: 1, customer: "Jonathan", total: "$150" },
    { id: 2, customer: "Alice", total: "$230" },
  ]);

  const handleDelete = (id) => {
    if (window.confirm("¿Eliminar orden?")) {
      setOrders(orders.filter((order) => order.id !== id));
    }
  };

  return (
    <Layout>
      <h2>Órdenes / Transacciones</h2>
      <ul>
        {orders.map((order) => (
          <li key={order.id}>
            Orden #{order.id} de {order.customer} - Total: {order.total}
            <button onClick={() => handleDelete(order.id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </Layout>
  );
};

export default Orders;
