import React, { useContext, useState, useMemo } from "react";
import Layout from "../components/layout/Layout";
import useFetch from "../hooks/useFetch";
import { CartContext } from "../contexts/CartContext";
import "./Store.css";

const Store = () => {
  // Obtenemos los datos de la API
  const { data, loading, error } = useFetch("https://pokeapi.co/api/v2/pokemon?limit=20");
  const { addToCart } = useContext(CartContext);

  // Estado para el mensaje de notificación
  const [notification, setNotification] = useState("");

  // useMemo para generar productos con precios aleatorios fijos
  const products = useMemo(() => {
    if (!data || !data.results) return [];
    return data.results.map((pokemon) => {
      const match = pokemon.url.match(/\/pokemon\/(\d+)\//);
      const id = match ? match[1] : null;
      const price = (Math.random() * (100 - 10) + 10).toFixed(2);
      return {
        id: pokemon.name, // Usamos el nombre como id único
        name: pokemon.name,
        price: parseFloat(price),
        image: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`
      };
    });
  }, [data]);

  // Función para añadir producto al carrito y mostrar la notificación
  const handleAddToCart = (product) => {
    addToCart(product);
    setNotification("Producto añadido al carrito");
    setTimeout(() => {
      setNotification("");
    }, 2000);
  };

  return (
    <Layout>
      <h2>Tienda de Pokémon</h2>
      {notification && <div className="notification">{notification}</div>}
      {loading && <p>Cargando...</p>}
      {error && <p>Error al cargar datos: {error.message}</p>}
      {products.length > 0 && (
        <div className="store-list">
          {products.map((product) => (
            <div key={product.id} className="store-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p className="price">${product.price}</p>
              <button onClick={() => handleAddToCart(product)}>
                Añadir al Carrito
              </button>
            </div>
          ))}
        </div>
      )}
    </Layout>
  );
};

export default Store;
