import React, { useContext } from 'react';
import { AppBar, Toolbar, Typography, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import { CartContext } from '../../contexts/CartContext';
import HomeIcon from '@mui/icons-material/Home';
import StoreIcon from '@mui/icons-material/Store';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import PeopleIcon from '@mui/icons-material/People';

const Layout = ({ children }) => {
  const { cart } = useContext(CartContext);
  const totalQuantity = cart.reduce((acc, item) => acc + item.quantity, 0);

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            PROYECTO FINAL Inicio
          </Typography>
          <Button color="inherit" component={Link} to="/">
            <HomeIcon sx={{ mr: 1 }} /> Inicio
          </Button>
          <Button color="inherit" component={Link} to="/store">
            <StoreIcon sx={{ mr: 1 }} /> Tienda
          </Button>
          <Button color="inherit" component={Link} to="/cart">
            <ShoppingCartIcon sx={{ mr: 1 }} /> Carrito ({totalQuantity})
          </Button>
          <Button color="inherit" component={Link} to="/users">
            <PeopleIcon sx={{ mr: 1 }} /> Usuarios
          </Button>
        </Toolbar>
      </AppBar>
      <main style={{ padding: '2rem' }}>
        {children}
      </main>
      <footer style={{ textAlign: 'center', padding: '1rem', background: '#f5f5f5' }}>
        &copy; 2025 PROYECTO FINAL. Todos los derechos reservados.
      </footer>
    </>
  );
};

export default Layout;
