import React, { useContext, useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { AuthContext } from "../contexts/AuthContext";
import Layout from "../components/layout/Layout";
import "./Dashboard.css";

const Dashboard = () => {
  const { logout, user } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  // Array de URLs de Pokémon (puedes ampliarlo)
  const pokemonImages = [
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/1.png",
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/4.png",
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/7.png",
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/25.png",
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/39.png",
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/94.png",
    "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/133.png"
  ];

  // Estado para la imagen aleatoria
  const [randomImage, setRandomImage] = useState("");

  useEffect(() => {
    const changeImage = () => {
      const randomIndex = Math.floor(Math.random() * pokemonImages.length);
      setRandomImage(pokemonImages[randomIndex]);
    };
    changeImage(); // inicial
    const interval = setInterval(changeImage, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <Layout>
      <div className="dashboard-container">
        <h2>Dashboard</h2>
        <p>Bienvenido, {user ? user.username : "Invitado"}.</p>
        <div className="hero">
          {randomImage && <img src={randomImage} alt="Pokemon Random" />}
          <p className="hero-text">¡Descubre nuestros productos y ofertas!</p>
        </div>
        <div className="dashboard-links">
          <Link to="/store" className="dashboard-link">Tienda</Link>
          <Link to="/cart" className="dashboard-link">Carrito</Link>
        </div>
        <button onClick={handleLogout} className="logout-btn">Cerrar Sesión</button>
      </div>
    </Layout>
  );
};

export default Dashboard;
