import React, { useContext } from "react";
import Layout from "../components/layout/Layout";
import useFetch from "../hooks/useFetch";
import { CartContext } from "../contexts/CartContext";
import "./Store.css";

const Store = () => {
  // Obtener 20 Pokémon de la PokeAPI
  const { data, loading, error } = useFetch("https://pokeapi.co/api/v2/pokemon?limit=20");
  const { addToCart } = useContext(CartContext);

  // Función para asignar un precio aleatorio entre 10 y 100
  const getRandomPrice = () => {
    return (Math.random() * (100 - 10) + 10).toFixed(2);
  };

  // Función para extraer el ID del URL de cada Pokémon
  const extractId = (url) => {
    const match = url.match(/\/pokemon\/(\d+)\//);
    return match ? match[1] : null;
  };

  return (
    <Layout>
      <h2>Tienda de Pokémon</h2>
      {loading && <p>Cargando...</p>}
      {error && <p>Error al cargar datos: {error.message}</p>}
      {data && data.results && (
        <div className="store-list">
          {data.results.map((pokemon) => {
            const id = extractId(pokemon.url);
            const price = getRandomPrice();
            return (
              <div key={pokemon.name} className="store-card">
                <img
                  src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`}
                  alt={pokemon.name}
                />
                <h3>{pokemon.name}</h3>
                <p className="price">${price}</p>
                <button
                  onClick={() =>
                    addToCart({
                      id: pokemon.name,
                      name: pokemon.name,
                      price: parseFloat(price),
                      image: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`
                    })
                  }
                >
                  Añadir al Carrito
                </button>
              </div>
            );
          })}
        </div>
      )}
    </Layout>
  );
};

export default Store;
