import React, { useContext, useMemo } from "react";
import Layout from "../components/layout/Layout";
import useFetch from "../hooks/useFetch";
import { CartContext } from "../contexts/CartContext";
import "./Store.css";

const Store = () => {
  // Llamamos a la API para obtener 20 Pokémon
  const { data, loading, error } = useFetch("https://pokeapi.co/api/v2/pokemon?limit=20");
  const { addToCart } = useContext(CartContext);

  // Función para extraer el ID del URL de cada Pokémon
  const extractId = (url) => {
    const match = url.match(/\/pokemon\/(\d+)\//);
    return match ? match[1] : null;
  };

  // Usamos useMemo para transformar la data y asignar un precio aleatorio fijo a cada producto.
  const products = useMemo(() => {
    if (!data || !data.results) return [];
    return data.results.map((pokemon) => {
      const id = extractId(pokemon.url);
      // Genera un precio aleatorio entre 10 y 100 y lo fija
      const price = (Math.random() * (100 - 10) + 10).toFixed(2);
      return {
        id: pokemon.name, // usamos el nombre como identificador único
        name: pokemon.name,
        price: parseFloat(price),
        image: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${id}.png`,
      };
    });
  }, [data]);

  return (
    <Layout>
      <h2>Tienda de Pokémon</h2>
      {loading && <p>Cargando...</p>}
      {error && <p>Error al cargar datos: {error.message}</p>}
      {products.length > 0 && (
        <div className="store-list">
          {products.map((product) => (
            <div key={product.id} className="store-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p className="price">${product.price}</p>
              <button onClick={() => addToCart(product)}>
                Añadir al Carrito
              </button>
            </div>
          ))}
        </div>
      )}
    </Layout>
  );
};

export default Store;
